import React, {Component, Fragment} from "react";
import {Row} from "reactstrap";
import IntlMessages from "../../../helpers/IntlMessages";
import {Colxx, Separator} from "../../../components/common/CustomBootstrap";
import Breadcrumb from "../../../containers/navs/Breadcrumb";
import axios from "axios";
import {servicePath, tokenPrefix} from "../../../constants/defaultValues";
import {NotificationManager} from "../../../components/common/react-notifications";
import errorMessage from "../../../constants/errorMessages";
import {connect} from "react-redux";

class ProductEdit extends Component {

    constructor(props) {
        super(props);
        this.state = {
            loading: false
        };
    }

    componentDidMount() {
        this.setState({loading: true});
        axios
            .get(servicePath + "/products/get", {
                params: {
                    productId: this.props.match.params.productId,
                },
                headers: {
                    'X-API-TOKEN': tokenPrefix + this.props.apiToken
                }
            })
            .then(response => response['data'])
            .then(data => {
                this.setState({loading: false});
                if (data['message'] === "") {
                    console.log('loading good');
                } else {
                    this.props.history.push('/products/products');
                }
            })
            .catch(error => {
                this.props.history.push('/products/products');
            });
    }

    render() {
        const {loading} = this.state;
        return (
            <Fragment>
                <Row>
                    <Colxx xxs="12">
                        <Breadcrumb heading="menu.product" match={this.props.match}/>
                        <Separator className="mb-5"/>
                    </Colxx>
                </Row>
                <Row>
                    <Colxx xxs="12" className="mb-4">
                        {
                            loading ? <div className="loading" /> :
                                <div>loaded</div>
                        }
                    </Colxx>
                </Row>
            </Fragment>
        )
    }
}

const mapStateToProps = ({authUser}) => {
    const {token: apiToken} = authUser;
    return {apiToken};
};

export default connect(
    mapStateToProps,
    {}
)(ProductEdit);
