import { PluralRules } from 'react-intl';
import enLang from './entries/en-US';
import esLang from './entries/es-ES';
import enRtlLang from './entries/en-US-rtl';

if (!Intl.PluralRules) {
    require('@formatjs/intl-pluralrules/polyfill');
    require('@formatjs/intl-pluralrules/dist/locale-data/de'); // Add locale data for de
}
if (!Intl.RelativeTimeFormat) {
    require('@formatjs/intl-relativetimeformat/polyfill');
    require('@formatjs/intl-relativetimeformat/dist/locale-data/de'); // Add locale data for de
}

const AppLocale = {
    en: enLang,
    es: esLang,
    enrtl:enRtlLang
};
PluralRules(AppLocale.en.data);
PluralRules(AppLocale.es.data);
PluralRules(AppLocale.enrtl.data);

export default AppLocale;
