import React, {Component, Fragment} from 'react';
import {Row, Input} from "reactstrap";
import Select from "react-select";
import {Colxx, Separator} from "../../../components/common/CustomBootstrap";
import Breadcrumb from "../../../containers/navs/Breadcrumb";
import IntlMessages from "../../../helpers/IntlMessages";
import ColorPicker from "../../../components/common/ColorPicker";
import {connect} from "react-redux";
import {loginUser} from "../../../redux/auth/actions";

class MenuAppSettings extends Component {

    constructor(props) {
        super(props);
        this.state = {
            'banner-color': this.props.user.banner_color,
            'category-bg-color': this.props.user.category_background_color,
            'product-color': this.props.user.product_background_color,
            'font-color': this.props.user.font_color,
        };
    }

    handleColorChanged = (id, color) => {
        this.setState({
            [id]: color
        });
    }

    saveColors = () => {
        console.log(this.state);
    }

    render() {
        const {
            template_no: templateNo,
            banner_color: bannerColor,
            category_background_color: categoryBgColor,
            product_background_color: productBgColor,
            font_color: fontColor
        } = this.props.user;

        return (
            <Fragment>
                <Row>
                    <Colxx xxs="12">
                        <Breadcrumb heading="menu.menu-app-settings" match={this.props.match}/>
                        <Separator className="mb-5"/>
                    </Colxx>
                </Row>
                <Row>
                    <Colxx xxs="12" className="mb-4">
                        <Row>
                            <Colxx sm="12" md="6">
                                <div className="card">
                                    <div className="card-body">
                                        <div className="card-title">
                                            <span><IntlMessages id="page.menu-app-settings.template"/></span>
                                        </div>

                                        <div className="mb-4">
                                            <h6 className="font-weight-light">
                                                <IntlMessages id="page.menu-app-settings.company-logo"/>
                                            </h6>

                                        </div>

                                        <div className="mb-4">
                                            <h6 className="font-weight-light">
                                                <IntlMessages id="page.menu-app-settings.template"/>
                                            </h6>
                                            <Select
                                                options={[
                                                    {label: "Template 1", value: 1},
                                                    {label: "Template 2", value: 2}
                                                ]}
                                            />
                                        </div>
                                    </div>
                                </div>
                            </Colxx>

                            <Colxx sm="12" md="6">
                                <div className="card">
                                    <div className="card-body">
                                        <div className="card-title">
                                            <span><IntlMessages id="page.menu-app-settings.colors"/></span>
                                        </div>

                                        <div className="mb-4">
                                            <h6 className="font-weight-light">
                                                <IntlMessages id="page.menu-app-settings.banner-background-color"/>
                                            </h6>
                                            <ColorPicker initialColor={bannerColor}
                                                         colorChanged={this.handleColorChanged}
                                                         id="banner-color"/>
                                        </div>

                                        <div className="mb-4">
                                            <h6 className="font-weight-light">
                                                <IntlMessages
                                                    id="page.menu-app-settings.category-list-background-color"/>
                                            </h6>
                                            <ColorPicker initialColor={categoryBgColor}
                                                         colorChanged={this.handleColorChanged}
                                                         id="category-bg-color"/>
                                        </div>

                                        <div className="mb-4">
                                            <h6 className="font-weight-light">
                                                <IntlMessages
                                                    id="page.menu-app-settings.product-list-background-color"/>
                                            </h6>
                                            <ColorPicker initialColor={productBgColor}
                                                         colorChanged={this.handleColorChanged}
                                                         id="product-bg-color"/>
                                        </div>

                                        <div className="mb-4">
                                            <h6 className="font-weight-light">
                                                <IntlMessages id="page.menu-app-settings.font-color"/>
                                            </h6>
                                            <ColorPicker initialColor={fontColor}
                                                         colorChanged={this.handleColorChanged}
                                                         id="font-color"/>
                                        </div>

                                    </div>
                                </div>
                            </Colxx>
                        </Row>
                        <button onClick={this.saveColors} className="btn btn-primary">save</button>
                    </Colxx>
                </Row>
            </Fragment>
        )
    }
}

const mapStateToProps = ({authUser}) => {
    const {user, loading, error} = authUser;
    return {user, loading, error};
};

export default connect(
    mapStateToProps,
    {
        loginUser
    }
)(MenuAppSettings);
