import React, {Component, Fragment} from 'react';
import {Row} from "reactstrap";
import {Colxx, Separator} from "../../../components/common/CustomBootstrap";
import Breadcrumb from "../../../containers/navs/Breadcrumb";
import IntlMessages from "../../../helpers/IntlMessages";
import ColorPicker from "../../../components/common/ColorPicker";
import {connect} from "react-redux";
import {loginUser} from "../../../redux/auth/actions";

class MenuAppSettings extends Component {

    constructor(props) {
        super(props);
        this.state = {
            'banner-color': this.props.user.banner_color,
            'category-bg-color': this.props.user.category_background_color,
            'product-color': this.props.user.product_background_color,
            'font-color': this.props.user.font_color,
        };
    }
    
    handleColorChanged = (id, color) => {
        this.setState({
           [id]: color
        });
        console.log(this.state);
    }

    render() {
        const {
            template_no: templateNo,
            banner_color: bannerColor,
            category_background_color: categoryBgColor,
            product_background_color: productBgColor,
            font_color: fontColor
        } = this.props.user;

        return (
            <Fragment>
                <Row>
                    <Colxx xxs="12">
                        <Breadcrumb heading="menu.menu-app-settings" match={this.props.match}/>
                        <Separator className="mb-5"/>
                    </Colxx>
                </Row>
                <Row>
                    <Colxx xxs="12" className="mb-4">
                        <h5>Colors</h5>
                        <div className="card">
                            <div className="card-body">

                                <div>
                                    <span><IntlMessages id="page.menu-app-settings.banner-background-color"/></span>
                                    <ColorPicker initialColor={bannerColor} colorChanged={this.handleColorChanged}
                                                 id="banner-color"/>
                                </div>

                                <div>
                                    <span><IntlMessages
                                        id="page.menu-app-settings.category-list-background-color"/></span>
                                    <ColorPicker initialColor={categoryBgColor} colorChanged={this.handleColorChanged}
                                                 id="category-bg-color"/>
                                </div>

                                <div>
                                    <span><IntlMessages
                                        id="page.menu-app-settings.product-list-background-color"/></span>
                                    <ColorPicker initialColor={productBgColor} colorChanged={this.handleColorChanged}
                                                 id="product-bg-color"/>
                                </div>

                                <div>
                                    <span><IntlMessages id="page.menu-app-settings.font-color"/></span>
                                    <ColorPicker initialColor={fontColor} colorChanged={this.handleColorChanged}
                                                 id="font-color"/>
                                </div>

                            </div>
                        </div>

                    </Colxx>
                </Row>
            </Fragment>
        )
    }
}

const mapStateToProps = ({authUser}) => {
    const {user, loading, error} = authUser;
    return {user, loading, error};
};

export default connect(
    mapStateToProps,
    {
        loginUser
    }
)(MenuAppSettings);
