import React, {Component, Fragment} from "react";
import {Row, Card, CardBody, Label, Input, FormGroup} from "reactstrap";
import IntlMessages from "../../../helpers/IntlMessages";
import {Colxx, Separator} from "../../../components/common/CustomBootstrap";
import Breadcrumb from "../../../containers/navs/Breadcrumb";
import axios from "axios";
import {productImagePath, servicePath, tokenPrefix} from "../../../constants/defaultValues";
import {NotificationManager} from "../../../components/common/react-notifications";
import errorMessage from "../../../constants/errorMessages";
import {connect} from "react-redux";
import ImageUploader from "react-images-upload";
import CustomSelectInput from "../../../components/common/CustomSelectInput";
import Select from "react-select";

class ProductEdit extends Component {

    constructor(props) {
        super(props);
        this.state = {
            loading: false,
            product: {},
            categoryList: [],
            currencyList: []
        };
    }

    componentDidMount() {
        this.setState({loading: true});
        axios
            .get(servicePath + "/products/get", {
                params: {
                    productId: this.props.match.params.productId,
                },
                headers: {
                    'X-API-TOKEN': tokenPrefix + this.props.apiToken
                }
            })
            .then(response => response['data'])
            .then(data => {
                if (data['message'] === "") {
                    this.setState({
                        loading: false,
                        product: data['data']['product'],
                        categoryList: data['data']['categoryList'],
                        currencyList: data['data']['currencyList']
                    });
                } else {
                    this.props.history.push('/products/products');
                }
            })
            .catch(error => {
                this.props.history.push('/products/products');
            });
    }

    render() {
        console.log(this.state);
        const {loading, product, categoryList, currencyList} = this.state;
        return (
            <Fragment>
                <Row>
                    <Colxx xxs="12">
                        <Breadcrumb heading="menu.product-edit" match={this.props.match}/>
                        <Separator className="mb-5"/>
                    </Colxx>
                </Row>
                <Row>
                    <Colxx xxs="12" className="mb-4">
                        {
                            loading ? <div className="loading" /> :

                                <Card className="mb-4">
                                    <CardBody>
                                        <Row>
                                            <Colxx xxs="12" xl="8" className="col-left">
                                                <FormGroup>
                                                    <Label for="name">Product Name</Label>
                                                    <Input type="text" id="name" value={product.name} />
                                                </FormGroup>
                                                <FormGroup>
                                                    <Label for="description">Description</Label>
                                                    <Input type="textarea" id="description" value={product.description}/>
                                                </FormGroup>
                                                <FormGroup>
                                                    <Label for="category">Category</Label>
                                                    <Select
                                                        id="category"
                                                        components={{Input: CustomSelectInput}}
                                                        className="react-select"
                                                        classNamePrefix="react-select"
                                                        onChange={this.handleTemplateChange}
                                                        options={categoryList}
                                                    />
                                                </FormGroup>
                                            </Colxx>
                                            <Colxx xxs="12" xl="4" className="col-right">
                                                {
                                                    product.picture != null ?
                                                        <img src={`${productImagePath}/appview/${product.picture}`}
                                                             className="card-img-top"
                                                             alt="Company Logo"/> : ""
                                                }

                                                <ImageUploader
                                                    withPreview={true}
                                                    onChange={this.onDropImage}
                                                    singleImage={true}
                                                    buttonText="Choose Image"
                                                />
                                            </Colxx>
                                        </Row>
                                    </CardBody>
                                </Card>
                        }
                    </Colxx>
                </Row>
            </Fragment>
        )
    }
}

const mapStateToProps = ({authUser}) => {
    const {token: apiToken} = authUser;
    return {apiToken};
};

export default connect(
    mapStateToProps,
    {}
)(ProductEdit);
