import {
    LOAD_PRODUCT_SUCCESS,
    LOAD_PRODUCT_FAIL,
    TOGGLE_PRODUCT_STATE_SUCCESS,
} from "../actions";

const INIT_STATE = {
    items: [],
    totalPage: 1,
    totalItem: 0,
};

export default (state = INIT_STATE, action) => {
    switch (action.type) {
        case LOAD_PRODUCT_SUCCESS:
            return {
                ...state,
                items: action.payload.data,
                totalItem: action.payload.totalItem,
                totalPage: action.payload.totalPage
            };
        case LOAD_PRODUCT_FAIL:
            return {
                ...state,
                items: [],
                totalItem: 0,
                totalPage: 1
            };
        case TOGGLE_PRODUCT_STATE_SUCCESS:
            return {
                ...state,
                items: updateProductState(state.items, action.payload),
            };
        default:
            return {...state};
    }
}

const updateProductState = (products, productId) => {
    return products.map(product => {
        if (product['id'] !== productId) return product;
        return {
            ...product,
            show_flag: 1 - product.show_flag
        }
    });
}
