import {
    LOAD_PRODUCT_SUCCESS,
    LOAD_PRODUCT_FAIL
} from "../actions";

const INIT_STATE = {
    products: []
};

export default (state = INIT_STATE, action) => {
    switch (action.type) {
        case LOAD_PRODUCT_SUCCESS:
            return {...state, products: action.payload};
        case LOAD_PRODUCT_FAIL:
            return {...state};
        default:
            return {...state};
    }
}
